mapper.update:
UPDATE seckill.seckill_product SET
    number = number - 1,
    gmt_modified = current_timestamp
WHERE id = #{id}
and gmt_start <= #{gmtKill}
and gmt_end >= #{gmtKill}
and number > 0

mapper.select:
select id, name, number, gmt_start, gmt_end
FROM  seckill.seckill_product
WHERE id = #{id}
